mapper.select:
		SELECT COUNT(*)
		  FROM FIELD
		  WHERE CATEGORY_NUM = #{categoryNum}
         AND FIELD_AREA LIKE '%' || #{fieldArea} || '%'
         AND MATCH_GENDER = #{matchGender}
         AND MATCH_LEVEL = #{matchLevel}
         AND FIELD_DATE = #{fieldDate}

 		SELECT COUNT(*)
		FROM RESERVATION
		WHERE FIELD_NO =  #{fieldNo}

 		SELECT COUNT(*)
		  FROM RESERVATION
		 WHERE FIELD_NO = #{fieldNo}
		   AND RES_USER_NO = #{resUserNo}
