mapper.update:
UPDATE TEAM_OFFER
   SET OFFER_COUNT = OFFER_COUNT + 1
 WHERE OFFER_NO = #{offerNo}

	UPDATE TEAM_OFFER
	   SET OFFER_STATUS = 'N'
 	 WHERE OFFER_NO = #{offerNo}

mapper.select:
SELECT COUNT(*)
  FROM TEAM_MEMBER
  WHERE TEAM_NO = #{teamNo}

