 	UPDATE TEAM_IMG
 		SET TEAM_ORIGIN_NAME = #{teamOriginName},
			TEAM_CHANGE_NAME = #{teamChangeName}
 	   	WHERE TEAM_IMG_NO = #{teamImgNo}
